import React, { useState, useEffect, useRef } from 'react';
import { registerVoice, loginVoice } from '../utils/api';

const VoiceRecorder = ({ mode, userData, handleStatus }) => {
    const [isRecording, setIsRecording] = useState(false);
    const [countdown, setCountdown] = useState(10);
    const [audioBlob, setAudioBlob] = useState(null);
    const [passphrase, setPassphrase] = useState('');
    
    // Updated refs for AudioWorklet
    const audioContextRef = useRef(null);
    const audioStreamRef = useRef(null);
    const audioWorkletNodeRef = useRef(null);
    const audioDataRef = useRef([]);
    const countdownRef = useRef(null);

    useEffect(() => {
        // Initialize AudioContext and load worklet on mount
        const initAudioWorklet = async () => {
            try {
                const audioContext = new (window.AudioContext || window.webkitAudioContext)({ sampleRate: 16000 });
                await audioContext.audioWorklet.addModule('/audioRecorderWorklet.js');
                audioContextRef.current = audioContext;
            } catch (err) {
                console.error('Failed to initialize audio worklet:', err);
            }
        };
        
        initAudioWorklet();
        
        return () => {
            cleanupRecording();
            if (audioContextRef.current) {
                audioContextRef.current.close();
            }
        };
    }, []);

    const cleanupRecording = () => {
        try {
            if (audioWorkletNodeRef.current) {
                audioWorkletNodeRef.current.disconnect();
                audioWorkletNodeRef.current = null;
            }
            if (audioStreamRef.current) {
                audioStreamRef.current.getTracks().forEach(track => track.stop());
            }
            if (countdownRef.current) {
                clearInterval(countdownRef.current);
            }
            audioDataRef.current = [];
            setIsRecording(false);
            setCountdown(10);
        } catch (err) {
            console.error('Error in cleanup:', err);
        }
    };

    const flattenAudioData = (chunks) => {
        const length = chunks.reduce((sum, chunk) => sum + chunk.length, 0);
        const result = new Float32Array(length);
        let offset = 0;
        for (let chunk of chunks) {
            result.set(chunk, offset);
            offset += chunk.length;
        }
        return result;
    };

    const floatTo16BitPCM = (float32Array) => {
        const buffer = new Int16Array(float32Array.length);
        for (let i = 0; i < float32Array.length; i++) {
            const s = Math.max(-1, Math.min(1, float32Array[i]));
            buffer[i] = s < 0 ? s * 0x8000 : s * 0x7FFF;
        }
        return buffer;
    };

    const createWavHeader = (totalSamples, sampleRate = 16000, numChannels = 1, bitsPerSample = 16) => {
        const byteRate = sampleRate * numChannels * (bitsPerSample / 8);
        const blockAlign = numChannels * (bitsPerSample / 8);
        const subChunk2Size = totalSamples * blockAlign;
        const chunkSize = 36 + subChunk2Size;

        const buffer = new ArrayBuffer(44);
        const view = new DataView(buffer);

        // RIFF chunk descriptor
        writeString(view, 0, 'RIFF');                     // ChunkID
        view.setUint32(4, chunkSize, true);              // ChunkSize
        writeString(view, 8, 'WAVE');                     // Format

        // fmt sub-chunk
        writeString(view, 12, 'fmt ');                    // Subchunk1ID
        view.setUint32(16, 16, true);                    // Subchunk1Size (16 for PCM)
        view.setUint16(20, 1, true);                     // AudioFormat (1 for PCM)
        view.setUint16(22, numChannels, true);           // NumChannels
        view.setUint32(24, sampleRate, true);            // SampleRate
        view.setUint32(28, byteRate, true);              // ByteRate
        view.setUint16(32, blockAlign, true);            // BlockAlign
        view.setUint16(34, bitsPerSample, true);         // BitsPerSample

        // data sub-chunk
        writeString(view, 36, 'data');                    // Subchunk2ID
        view.setUint32(40, subChunk2Size, true);         // Subchunk2Size

        return buffer;
    };

    const writeString = (view, offset, string) => {
        for (let i = 0; i < string.length; i++) {
            view.setUint8(offset + i, string.charCodeAt(i));
        }
    };

    const startRecording = async () => {
        try {
            if (!audioContextRef.current) {
                throw new Error('AudioContext not initialized');
            }

            cleanupRecording();

            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            audioStreamRef.current = stream;

            if (audioContextRef.current.state === 'suspended') {
                await audioContextRef.current.resume();
            }

            const source = audioContextRef.current.createMediaStreamSource(stream);
            const workletNode = new AudioWorkletNode(audioContextRef.current, 'audio-recorder-worklet');

            audioDataRef.current = [];

            workletNode.port.onmessage = (event) => {
                if (event.data.eventType === 'data') {
                    audioDataRef.current.push(new Float32Array(event.data.audioData));
                }
            };

            source.connect(workletNode);
            workletNode.connect(audioContextRef.current.destination);

            audioWorkletNodeRef.current = workletNode;
            setIsRecording(true);
            startCountdown();

        } catch (err) {
            console.error('Error starting recording:', err);
            handleStatus('Failed to start recording. Please check microphone permissions.', 'error');
        }
    };

    const stopRecording = () => {
        try {
            if (audioWorkletNodeRef.current) {
                audioWorkletNodeRef.current.disconnect();
            }
            if (audioStreamRef.current) {
                audioStreamRef.current.getTracks().forEach(track => track.stop());
            }

            const flatData = flattenAudioData(audioDataRef.current);
            const pcm = floatTo16BitPCM(flatData);
            const header = createWavHeader(pcm.length, 16000);

            const wavBuffer = new Uint8Array(header.byteLength + pcm.byteLength);
            wavBuffer.set(new Uint8Array(header), 0);
            wavBuffer.set(new Uint8Array(pcm.buffer), header.byteLength);

            const wavBlob = new Blob([wavBuffer], { type: 'audio/wav' });
            setAudioBlob(wavBlob);
            processRecording(wavBlob);

            cleanupRecording();

        } catch (err) {
            console.error('Error stopping recording:', err);
            handleStatus('Failed to stop recording.', 'error');
        }
    };

    const startCountdown = () => {
        setCountdown(10);
        countdownRef.current = setInterval(() => {
            setCountdown(prev => {
                if (prev <= 1) {
                    clearInterval(countdownRef.current);
                    stopRecording();
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);
    };

    const processRecording = async (blob) => {
        try {
            let response;
            if (mode === 'register') {
                response = await registerVoice(blob, userData);
            } else {
                response = await loginVoice(blob, userData.username);
            }
            
            if (response.success) {
                setPassphrase(response.passphrase || '');
                handleStatus(response.message, 'success');
            } else {
                handleStatus(response.message, 'error');
            }
        } catch (err) {
            console.error('Error processing recording:', err);
            handleStatus('Failed to process recording. Please try again.', 'error');
        }
    };

    return (
        <div className="panel">
            <div className="panel-title">
                <i className="fas fa-microphone"></i>
                <h2>{mode === 'register' ? 'Voice Registration' : 'Voice Authentication'}</h2>
            </div>
            
            <div className="buttons">
                <button 
                    className={`btn btn-primary ${isRecording ? 'disabled' : ''}`}
                    onClick={startRecording}
                    disabled={isRecording}
                >
                    <i className="fas fa-microphone"></i> 
                    {mode === 'register' ? 'Record Passphrase' : 'Verify Identity'}
                </button>
                {isRecording && (
                    <button 
                        className="btn btn-danger"
                        onClick={stopRecording}
                    >
                        <i className="fas fa-stop"></i> Stop Recording
                    </button>
                )}
            </div>
            
            {isRecording && (
                <>
                    <div className="recording-indicator">
                        <div className="pulse"></div>
                        <div>Recording your voice... Please speak now</div>
                    </div>
                    <div className="timer">{countdown}</div>
                    <div className="voice-wave">
                        {[...Array(8)].map((_, i) => (
                            <div key={i} className="wave-bar"></div>
                        ))}
                    </div>
                </>
            )}
            
            {passphrase && (
                <div className="result-box">
                    <div className="result-text">
                        Your {mode === 'register' ? 'registered' : 'recognized'} passphrase: 
                        <span> "{passphrase}"</span>
                    </div>
                </div>
            )}
        </div>
    );
};

export default VoiceRecorder;
