const API_BASE = 'http://localhost:5000/api';

const validateAudioBlob = async (blob) => {
    if (!blob) {
        throw new Error('No audio data available');
    }
    if (!(blob instanceof Blob)) {
        throw new Error('Invalid audio data format');
    }
    if (blob.size === 0) {
        throw new Error('Audio file is empty');
    }
    // Check if it's actually a WAV file by reading the header
    const buffer = await blob.arrayBuffer();
    const view = new DataView(buffer);
    const riffHeader = String.fromCharCode(
        view.getUint8(0),
        view.getUint8(1),
        view.getUint8(2),
        view.getUint8(3)
    );
    if (riffHeader !== 'RIFF') {
        throw new Error('Invalid WAV file format');
    }
};

export const registerVoice = async (audioBlob, userData) => {
    try {
        await validateAudioBlob(audioBlob);
        
        const formData = new FormData();
        formData.append('audio', audioBlob, 'recording.wav');
        
        for (const [key, value] of Object.entries(userData)) {
            formData.append(key, value);
        }
        
        const response = await fetch(`${API_BASE}/register`, {
            method: 'POST',
            body: formData
        });

        const data = await response.json();
        
        if (!response.ok) {
            throw new Error(data.message || 'Server error during registration');
        }
        
        return data;
    } catch (err) {
        console.error('Registration error:', err);
        return { 
            success: false, 
            message: err.message || 'Failed to register voice' 
        };
    }
};

export const loginVoice = async (audioBlob, username) => {
    try {
        await validateAudioBlob(audioBlob);
        
        const formData = new FormData();
        formData.append('audio', audioBlob, 'recording.wav');
        formData.append('username', username);
        
        const response = await fetch(`${API_BASE}/login`, {
            method: 'POST',
            body: formData
        });

        const data = await response.json();
        
        if (!response.ok) {
            throw new Error(data.message || 'Server error during login');
        }
        
        return data;
    } catch (err) {
        console.error('Login error:', err);
        return { 
            success: false, 
            message: err.message || 'Failed to authenticate' 
        };
    }
};
