import React, { useState, useEffect, useRef } from 'react';
import { registerVoice, loginVoice } from '../utils/api';

const VoiceRecorder = ({ mode, userData, handleStatus }) => {
    const [isRecording, setIsRecording] = useState(false);
    const [countdown, setCountdown] = useState(10);
    const [audioBlob, setAudioBlob] = useState(null);
    const [passphrase, setPassphrase] = useState('');
    const mediaRecorderRef = useRef(null);
    const audioContextRef = useRef(null);
    const audioChunksRef = useRef([]);
    const countdownRef = useRef(null);

    // Get supported MIME type
    const getSupportedMimeType = () => {
        const types = [
            'audio/webm',
            'audio/webm;codecs=opus',
            'audio/ogg;codecs=opus',
            'audio/mp4;codecs=opus'
        ];
        return types.find(type => MediaRecorder.isTypeSupported(type)) || 'audio/webm';
    };

    // Initialize AudioContext
    const initAudioContext = () => {
        if (!audioContextRef.current || audioContextRef.current.state === 'closed') {
            audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)({
                sampleRate: 16000
            });
        }
        return audioContextRef.current;
    };

    useEffect(() => {
        return () => {
            cleanupRecording();
        };
    }, []);

    const cleanupRecording = () => {
        try {
            if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
                mediaRecorderRef.current.stop();
            }
            if (mediaRecorderRef.current && mediaRecorderRef.current.stream) {
                mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
            }
            if (countdownRef.current) {
                clearInterval(countdownRef.current);
            }
            mediaRecorderRef.current = null;
            audioChunksRef.current = [];
            setIsRecording(false);
            setCountdown(10);
        } catch (err) {
            console.error('Error in cleanup:', err);
        }
    };

    const createSimpleWavFromBlob = async (blob) => {
        const arrayBuffer = await blob.arrayBuffer();
        const wavHeader = createWavHeader(arrayBuffer.byteLength);
        const fullBuffer = new Uint8Array(wavHeader.byteLength + arrayBuffer.byteLength);
        fullBuffer.set(new Uint8Array(wavHeader), 0);
        fullBuffer.set(new Uint8Array(arrayBuffer), wavHeader.byteLength);
        return new Blob([fullBuffer], { type: 'audio/wav' });
    };

    const floatTo16BitPCM = (float32Array) => {
        const buffer = new Int16Array(float32Array.length);
        for (let i = 0; i < float32Array.length; i++) {
            const s = Math.max(-1, Math.min(1, float32Array[i]));
            buffer[i] = s < 0 ? s * 0x8000 : s * 0x7FFF;
        }
        return buffer;
    };

    const createWavHeader = (totalSamples, sampleRate = 16000, numChannels = 1, bitsPerSample = 16) => {
        const byteRate = (sampleRate * numChannels * bitsPerSample) / 8;
        const blockAlign = (numChannels * bitsPerSample) / 8;
        const buffer = new ArrayBuffer(44);
        const view = new DataView(buffer);

        // RIFF identifier
        writeString(view, 0, 'RIFF');
        // RIFF chunk length
        view.setUint32(4, 36 + totalSamples * blockAlign, true);
        // RIFF type
        writeString(view, 8, 'WAVE');
        // format chunk identifier
        writeString(view, 12, 'fmt ');
        // format chunk length
        view.setUint32(16, 16, true);
        // sample format (raw)
        view.setUint16(20, 1, true);
        // channel count
        view.setUint16(22, numChannels, true);
        // sample rate
        view.setUint32(24, sampleRate, true);
        // byte rate (sample rate * block align)
        view.setUint32(28, byteRate, true);
        // block align (channel count * bytes per sample)
        view.setUint16(32, blockAlign, true);
        // bits per sample
        view.setUint16(34, bitsPerSample, true);
        // data chunk identifier
        writeString(view, 36, 'data');
        // data chunk length
        view.setUint32(40, totalSamples * blockAlign, true);

        return buffer;
    };

    const writeString = (view, offset, string) => {
        for (let i = 0; i < string.length; i++) {
            view.setUint8(offset + i, string.charCodeAt(i));
        }
    };

    const startRecording = async () => {
        try {
            cleanupRecording();

            // Initialize AudioContext first
            const audioContext = initAudioContext();
            if (audioContext.state === 'suspended') {
                await audioContext.resume();
            }

            const stream = await navigator.mediaDevices.getUserMedia({
                audio: {
                    channelCount: 1,
                    sampleRate: 16000,
                    echoCancellation: true,
                    noiseSuppression: true,
                    autoGainControl: true
                }
            });

            // Create an audio source from the stream
            const sourceNode = audioContext.createMediaStreamSource(stream);
            
            // Create a media recorder with supported format
            const mediaRecorder = new MediaRecorder(stream, {
                mimeType: getSupportedMimeType(),
                audioBitsPerSecond: 16000
            });

            mediaRecorderRef.current = mediaRecorder;
            audioChunksRef.current = [];

            mediaRecorder.ondataavailable = (e) => {
                if (e.data && e.data.size > 0) {
                    audioChunksRef.current.push(e.data);
                }
            };

            mediaRecorder.onerror = (event) => {
                console.error('Recording error:', event.error);
                handleStatus('Error during recording. Please try again.', 'error');
                cleanupRecording();
            };

            mediaRecorder.onstop = async () => {
                try {
                    if (audioChunksRef.current.length === 0) {
                        throw new Error('No audio data recorded');
                    }

                    // Ensure AudioContext is ready
                    const audioContext = initAudioContext();
                    if (audioContext.state === 'suspended') {
                        await audioContext.resume();
                    }

                    // Create blob with recorded data
                    const recordedBlob = new Blob(audioChunksRef.current, { type: getSupportedMimeType() });
                    
                    // Convert blob to array buffer
                    const arrayBuffer = await recordedBlob.arrayBuffer();
                    
                    try {
                        // Decode the audio data
                        const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

                        // Convert to mono if needed
                        const monoData = audioBuffer.numberOfChannels > 1 
                            ? audioBuffer.getChannelData(0).map((sample, i) => {
                                return Array.from({length: audioBuffer.numberOfChannels}, (_, channel) => 
                                    audioBuffer.getChannelData(channel)[i]
                                ).reduce((a, b) => a + b) / audioBuffer.numberOfChannels;
                            })
                            : audioBuffer.getChannelData(0);

                        // Convert to 16-bit PCM
                        const pcmData = floatTo16BitPCM(monoData);

                        // Create WAV header
                        const wavHeader = createWavHeader(pcmData.length);
                        
                        // Combine header and data
                        const wavBuffer = new Uint8Array(wavHeader.byteLength + pcmData.byteLength);
                        wavBuffer.set(new Uint8Array(wavHeader), 0);
                        wavBuffer.set(new Uint8Array(pcmData.buffer), wavHeader.byteLength);

                        const wavBlob = new Blob([wavBuffer], { type: 'audio/wav' });
                        setAudioBlob(wavBlob);
                        await processRecording(wavBlob);
                    } catch (decodeError) {
                        console.error('Error decoding audio:', decodeError);
                        
                        // Fallback: try to use the original blob if decoding fails
                        try {
                            // Create a simple WAV wrapper around the recorded data
                            const wavBlob = await createSimpleWavFromBlob(recordedBlob);
                            setAudioBlob(wavBlob);
                            await processRecording(wavBlob);
                        } catch (fallbackError) {
                            console.error('Fallback conversion failed:', fallbackError);
                            throw new Error('Could not process audio data. Please try recording again.');
                        }
                    }
                } catch (error) {
                    console.error('Error processing audio:', error);
                    handleStatus('Failed to process recording. Please try again.', 'error');
                } finally {
                    cleanupRecording();
                }
            };

            mediaRecorder.start(100);
            setIsRecording(true);
            startCountdown();

        } catch (err) {
            console.error('Error starting recording:', err);
            handleStatus('Failed to start recording. Please check microphone permissions.', 'error');
            cleanupRecording();
        }
    };

    const startCountdown = () => {
        setCountdown(10);
        countdownRef.current = setInterval(() => {
            setCountdown(prev => {
                if (prev <= 1) {
                    clearInterval(countdownRef.current);
                    stopRecording();
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);
    };

    const stopRecording = () => {
        try {
            if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
                mediaRecorderRef.current.stop();
            } else {
                cleanupRecording();
            }
        } catch (err) {
            console.error('Error stopping recording:', err);
            handleStatus('Error stopping recording. Please try again.', 'error');
            cleanupRecording();
        }
    };

    const processRecording = async (blob) => {
        try {
            let response;
            if (mode === 'register') {
                response = await registerVoice(blob, userData);
            } else {
                response = await loginVoice(blob, userData.username);
            }
            
            if (response.success) {
                setPassphrase(response.passphrase || '');
                handleStatus(response.message, 'success');
            } else {
                handleStatus(response.message, 'error');
            }
        } catch (err) {
            console.error('Error processing recording:', err);
            handleStatus('Failed to process recording. Please try again.', 'error');
        }
    };

    return (
        <div className="panel">
            <div className="panel-title">
                <i className="fas fa-microphone"></i>
                <h2>{mode === 'register' ? 'Voice Registration' : 'Voice Authentication'}</h2>
            </div>
            
            <div className="buttons">
                <button 
                    className={`btn btn-primary ${isRecording ? 'disabled' : ''}`}
                    onClick={startRecording}
                    disabled={isRecording}
                >
                    <i className="fas fa-microphone"></i> 
                    {mode === 'register' ? 'Record Passphrase' : 'Verify Identity'}
                </button>
                {isRecording && (
                    <button 
                        className="btn btn-danger"
                        onClick={stopRecording}
                    >
                        <i className="fas fa-stop"></i> Stop Recording
                    </button>
                )}
            </div>
            
            {isRecording && (
                <>
                    <div className="recording-indicator">
                        <div className="pulse"></div>
                        <div>Recording your voice... Please speak now</div>
                    </div>
                    <div className="timer">{countdown}</div>
                    <div className="voice-wave">
                        {[...Array(8)].map((_, i) => (
                            <div key={i} className="wave-bar"></div>
                        ))}
                    </div>
                </>
            )}
            
            {passphrase && (
                <div className="result-box">
                    <div className="result-text">
                        Your {mode === 'register' ? 'registered' : 'recognized'} passphrase: 
                        <span> "{passphrase}"</span>
                    </div>
                </div>
            )}
        </div>
    );
};

export default VoiceRecorder;
